INSERT INTO `fm_raw_plans` VALUES (99,'CD082','Aeronca 7BC Champ','F/F Rubber Scale','Sandor, Tom','','35.5\"','04/99','$13.00','Scale rubber power freeflight plane can be built with interchangeable wheels, floats or skis. Plans show all three options, and all three fly great. weighs 3.1 ounces. Plans on 2 sheets.','');
INSERT INTO `fm_raw_plans` VALUES (100,'CD083','Super Chips Akro II','R/C Sport','Shepherd, Brad','.60-.76','66\"','05/99','$22.00','Designed for a .60-.76 2-stroke or a .70-.91 4-stroke, this ship can be used for IMAC practice or competition. Complete instructions come with purchase of plan. Plans on 2 sheets.','');
INSERT INTO `fm_raw_plans` VALUES (101,'CD084','Hawker Hunter','C/L Stunt','Sunderlund Sr., Jack','.35-.40 2-cycle','53\"','05/99','$9.00','First designed and built in 1968, this semi-scale Stunt ship of the British Hunter fighter jet qualifies for VSC competition. The ninth in the Classic Stunter Series. Purchase of plans include complete text of instructions.','');
INSERT INTO `fm_raw_plans` VALUES (102,'CD085','Alexander Eaglerock Bullet','F/F Electric','Rees, Dave','','28-3/4\"','05/99','$9.00','One of the first corporate planes, this freeflight scale version of the single engine monoplane uses the HiLine Mini-6 electric motor. It can also be converted to micro R/C.','');
