INSERT INTO `fm_raw_plans` VALUES (106,'CD089','Gotcha Psycho','C/L Combat','Cartier, Phil','.32-.35','48\"','07/99','$9.00','Uses lots of the latest Combat technology, foam wings and carbon fiber boom. Winner at 1998 Nats. For Fast or Slow Combat','');
INSERT INTO `fm_raw_plans` VALUES (107,'CD090','Avia B35','F/F Rubber','Peters, Tony','','18-3/4\"','07/99','$4.50','Rarely modeled, WW II Czech trainer is a great outdoor flyer. Inc an even do some indoor service in a large space. Uses two strands of 3/32-inch Tan II rubber for power.','');
