INSERT INTO `fm_raw_plans` VALUES (109,'CD092','Stunt Goat .09','C/L Stunt','Clough Jr., Roy','.09','30\"','08/99','$7.00','Easy-going but pretty able C/L aerobat. Extremely easy to build profile model. Needs a Cox TeeDee or Medallion .09 to pull it around on .020 lines.','');
