INSERT INTO `rcm_raw_plans` VALUES ('fsp06641','Sidewinder Pylon Racer','WS: 44 in.; L: 48 in.; engine: .19+; 3 channels; 1 sheet. ','Dale Nutter','$14.95','MAN','','','Originally designed for the old AMA single-plane pylon racing, this model remains an interesting R/C sport plane. The Dale Nutter design has a remarkable delta configuration. Easy construction of conventional materials make it highly maneuverable. WS: 44 in.; L: 48 in.; engine: .19+; 3 channels; 1 sheet. ','fsp06641.jpg');
